import React, { useEffect, useState } from 'react';
import Layout from '../components/Layout';
import { SearchOutlined } from '@ant-design/icons';
import axios from 'axios';
import { Table, message } from 'antd';
import html2pdf from 'html2pdf.js'; // Import html2pdf library

const AppManager = () => {
  const [appointments, setAppointments] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const formattedDate = date.toISOString().split('T')[0];
    return formattedDate;
  };

  useEffect(() => {
    const fetchAppointmentsData = async () => {
      try {
        const response = await axios.get("http://localhost:3002/api/patient/get-appointment");
        setAppointments(response.data);
      } catch (error) {
        message.error("Failed to fetch Appointment data");
        console.error(error);
      }
    };
    
    fetchAppointmentsData();
  }, []);

  // Handle Search
  const handleSearch = async () => {
    const results = appointments.filter(item => item.Name.toLowerCase().includes(searchQuery.toLowerCase()));
    setAppointments(results);
  };

  const generateReport = () => {
    // Generate PDF
    const element = document.getElementById('pdf-content');
    html2pdf().from(element).save();
  };

  const columns = [
    {
      title: "Name",
      dataIndex: "Name",
      key: "Name",
    },
    {
      title: "Phone",
      dataIndex: "phoneNumber",
      key: "phoneNumber",
    },
    {
      title:"email",
      dataIndex:"email",
      key:"email",
    },
    {
      title: "Service",
      dataIndex: "service",
      key: "service",
    },
    {
      title: "Doctor",
      dataIndex: "doctor",
      key: "doctor",
    },
    {
      title: "Date",
      dataIndex: "date",
      key: "date",
      render: (date) => <span>{formatDate(date)}</span>,
    },
  
  ]; 

  return (
    <Layout>
      <h1 className='page-title'>Appointments</h1>
      <div className='search'>
        <input 
          className='searchInput'
          placeholder=' Search by Name'
          prefix={<SearchOutlined />}
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{ width:"250px", height: "30px", border:"none", boxShadow:"none", borderRadius:'50px', marginLeft: '15px' }}
        />
        <SearchOutlined
          style={{ fontSize: "20px", cursor: "pointer", padding:"2.5px" }}
          onClick={handleSearch}
        />
      </div>
      <Table 
        columns={columns} 
        dataSource={appointments.map(appointment => ({ ...appointment, key: appointment._id }))} 
      />
      {/* Include content for PDF generation */}
      <div id="pdf-content" style={{ display: "none" }}>
        <h1>Appointments</h1>
        <Table 
          columns={columns} 
          dataSource={appointments} 
          pagination={false} // Disable pagination in PDF
        />
      </div>
    </Layout>
  );
};

export default AppManager;
